import React from 'react';
import { Container, Row, Card, Button } from 'react-bootstrap';
import ReactDOM from 'react-dom';
import './index.css';
import StockComp from './StockComp.js'

class DisplayList extends React.Component {

    constructor(props) {
        super(props)
        this.cart = []
    }

    addToCartDisp(item) {
        this.cart.push(item)
        console.log(this.cart)
    }

    render() {
        return (
            <div>
                <Container>
                    <Row className="justify-content-md-center">
                    {this.props.list.map(item => 
                        <Card key={item.tag} className="stockCard">
                            <Card.Body>
                                <Card.Title>{item.name}</Card.Title>
                                <Card.Subtitle className="mb-2 text-muted">{item.tag}</Card.Subtitle>
                                <Card.Text><b>Current Price: </b>${item.stockPrice}</Card.Text>
                                <Card.Text><b>Performance: </b>{item.perform}%</Card.Text>
                                <Card.Text><b>Sector: </b>{item.sector}</Card.Text>
                                <Button onClick={() => this.addToCart(item)}>Purchase Stock</Button>
                            </Card.Body>
                        </Card>
                    )}
                    </Row>
                </Container>

                <h1>Your Stocks</h1>

                <Container>
                    <Row className="justify-content-md-center">
                    {this.cart.map(item => 
                        <Card key={item.tag} className="stockCard">
                            <Card.Body>
                                <Card.Title>{item.name}</Card.Title>
                                <Card.Subtitle className="mb-2 text-muted">{item.tag}</Card.Subtitle>
                                <Card.Text><b>Current Price: </b>${item.stockPrice}</Card.Text>
                                <Card.Text><b>Performance: </b>{item.perform}%</Card.Text>
                                <Card.Text><b>Sector: </b>{item.sector}</Card.Text>
                                <Button onClick={() => this.addToCart(item)}>Purchase Stock</Button>
                            </Card.Body>
                        </Card>
                            )}
                    </Row>
                </Container>

            </div>
        )
    }
}

export default DisplayList;
