import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import Fruit from './fruit.js';

import { AwesomeButton } from "react-awesome-button";
import "react-awesome-button/dist/styles.css";

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      items: [
      {name: "Yeets", color: "Red", price: 1.00},
      {name: "Pears", color: "Green", price: 2.00},
      {name: "Oranges", color: "Orange", price: 3.00}
      ],
      discount: false 
    };
  }

  discountMe = () => {
    let discountAmount = 0.5
    if (this.state.discount) {
      discountAmount = 2
    }

    this.state.items.map(
      (fruit) => fruit.price = discountAmount * fruit.price
    )

    this.setState ( 
      {
      discount: !this.state.discount
      }
    )
  }

  render() {
    return (
      <div className="main">
        {this.state.items.map(
          (fruit) => <Fruit name={fruit.name}
                            color={fruit.color}
                            price={fruit.price}></Fruit>
        )
        }
        <AwesomeButton onPress={this.discountMe}>Hide/Show Discount</AwesomeButton>
      </div>
    )
  }
}

export default App;
