import React from 'react';
import ReactDOM from 'react-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Dropdown, NavItem } from 'react-bootstrap';
import { Nav, Navbar, NavDropdown, Form, FormControl, Button, ButtonGroup, DropdownButton } from 'react-bootstrap';
import DisplayList from './DisplayList.js'
import DisplayCart from './DisplayCart.js'
import './index.css';

class FilteredList extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            sector: "All",
            perform: "All",
            sort: "Name",
            allItems: this.props.list,
        };
        this.cart = []
    }

    addToCart(item) {
        this.cart.push(item)
        console.log(this.cart)
    }

    onSelectReset = event => {
        this.setState ({
            sector: "All",
            perform: "All",
            sort: "Name"
        })
    }

    onSelectFilterSector = event => {
        this.setState ({
            sector: event
        })
    };

    onSelectFilterPerform = event => {
        this.setState ({
            perform: event
        })
    };

    onSelectSort = event => {
        this.setState ({
            sort: event
        })
    };

    matchesFilterSector = item =>  {
        if(this.state.sector === "All") { 
            return true
        } else if (this.state.sector === item.sector) {
            return true
        } else {
            return false
        }
    }

    matchesFilterPerform = item =>  {
        if(this.state.perform === "All") { 
            return true
        } else if (this.state.perform === "Positive") {
            if(item.perform > 0) {
                return true
            } else {
                return false
            }
        } else if(this.state.perform === "Negative") {
            if(item.perform > 0) {
                return false
            } else {
                return true
            }
        } else {
            return false
        }
    }

    matchesSortName(a,b) {
        var nameA = a.name.toUpperCase();
        var nameB = b.name.toUpperCase();
        if (nameA < nameB) {
          return -1;
        }
        if (nameA > nameB) {
          return 1;
        }
        return 0;
    };

    matchesSortPrice1(a, b) {
        return a.stockPrice - b.stockPrice; 
    }

    matchesSortPrice2(a, b) {
        return b.stockPrice - a.stockPrice; 
    }

    displayMe(myList) {
        myList = myList.filter(this.matchesFilterSector)
        myList = myList.filter(this.matchesFilterPerform)
        if(this.state.sort === "Name") {
            return myList.sort(this.matchesSortName)
        }
        else if(this.state.sort === "Price1") {
            return myList.sort(this.matchesSortPrice1)
        }
        else if(this.state.sort === "Price2") {
            return myList.sort(this.matchesSortPrice2)
        }
        else{
            return myList
        }
    }

    render() {
        this.cart = [this.state.allItems]
        return (
            <div>
            {/* <Navbar bg="light" expand="lg">
                <Navbar.Brand>Stock Performance</Navbar.Brand>
                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="mr-auto">
                        <NavDropdown title="Stock Performance" id="sector">
                            <NavDropdown.Item eventKey="Positive" onSelect={this.onSelectFilterPerform}>Positive</NavDropdown.Item>
                            <NavDropdown.Item eventKey="Negative" onSelect={this.onSelectFilterPerform}>Negative</NavDropdown.Item>
                            <NavDropdown.Divider/>
                            <NavDropdown.Item eventKey="All" onSelect={this.onSelectFilterPerform}>All</NavDropdown.Item>
                        </NavDropdown>
                        <NavDropdown title="Sector" id="sector">
                            <NavDropdown.Item eventKey="Food" onSelect={this.onSelectFilterSector}>Food</NavDropdown.Item>
                            <NavDropdown.Item eventKey="Technology" onSelect={this.onSelectFilterSector}>Technology</NavDropdown.Item>
                            <NavDropdown.Item eventKey="Airline" onSelect={this.onSelectFilterSector}>Airlines</NavDropdown.Item>
                            <NavDropdown.Divider/>
                            <NavDropdown.Item eventKey="All" onSelect={this.onSelectFilterSector}>All</NavDropdown.Item>
                        </NavDropdown>
                        <NavDropdown title="Sort By" id="sort_by">
                            <NavDropdown.Item eventKey="Name" onSelect={this.onSelectSort}>Name (A to Z)</NavDropdown.Item>
                            <NavDropdown.Divider />
                            <NavDropdown.Item eventKey="Price2" onSelect={this.onSelectSort}>Price (Highest to Lowest)</NavDropdown.Item>
                            <NavDropdown.Item eventKey="Price1" onSelect={this.onSelectSort}>Price (Lowest to Highest)</NavDropdown.Item>
                        </NavDropdown>
                        <Nav.Link eventKey="None" onSelect={this.onSelectReset}>Reset All</Nav.Link>
                    </Nav>
                </Navbar.Collapse>
            </Navbar> */}

            <Navbar bg="light" expand="lg">
                <Navbar.Brand>Sector</Navbar.Brand>
                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="mr-auto">
                        <Nav.Link eventKey="All" onSelect={this.onSelectFilterSector}>All</Nav.Link>
                        <Nav.Link eventKey="Food" onSelect={this.onSelectFilterSector}>Food</Nav.Link>
                        <Nav.Link eventKey="Technology" onSelect={this.onSelectFilterSector}>Technology</Nav.Link>
                        <Nav.Link eventKey="Airline" onSelect={this.onSelectFilterSector}>Airline</Nav.Link>
                    </Nav>
                </Navbar.Collapse>
            </Navbar>

            <Navbar bg="light" expand="lg">
                <Navbar.Brand>Performance</Navbar.Brand>
                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="mr-auto">
                        <Nav.Link eventKey="All" onSelect={this.onSelectFilterPerform}>All</Nav.Link>
                        <Nav.Link eventKey="Positive" onSelect={this.onSelectFilterPerform}>Positive</Nav.Link>
                        <Nav.Link eventKey="Negative" onSelect={this.onSelectFilterPerform}>Negative</Nav.Link>
                    </Nav>
                </Navbar.Collapse>
            </Navbar>

            <Navbar bg="light" expand="lg">
                <Nav className="mr-auto">
                    <NavDropdown title="Sort By" id="sort_by" class="dropdown-toggle nav-link">
                        <NavDropdown.Item eventKey="Name" onSelect={this.onSelectSort}>Name (A to Z)</NavDropdown.Item>
                        <NavDropdown.Divider />
                        <NavDropdown.Item eventKey="Price2" onSelect={this.onSelectSort}>Price (Highest to Lowest)</NavDropdown.Item>
                        <NavDropdown.Item eventKey="Price1" onSelect={this.onSelectSort}>Price (Lowest to Highest)</NavDropdown.Item>
                    </NavDropdown>
                    <Nav.Link eventKey="None" onSelect={this.onSelectReset}>Reset All</Nav.Link>
                </Nav>
            </Navbar>

            <DisplayList list={this.displayMe(this.state.allItems)} callback={this.addToCart.bind(this)}/> 

            <h1>{this.cart}</h1>

            {/* <DisplayCart cart={this.state.cart}/> */}
            </div>
        )
    }
}

export default FilteredList;
