import React from 'react';
import ReactDOM from 'react-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Dropdown } from 'react-bootstrap';
import DisplayList from './DisplayList.js'

class FilteredList extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            sector: "All"
        };
    }

    onSelectFilterSize = event => {
        this.setState ({
            sector: event
        })
    };

    matchesFilterSize = item =>  {

        console.log(this.state.sector)

        if(this.state.sector === "All") { 
            return true
        } else if (this.state.sector === item.sector) {
            return true
        } else {
            return false
        }
    }

    render() {
        return (
            <div>
            <Dropdown>
                <Dropdown.Toggle variant="success" id="dropdown-basic">
                Dropdown
                </Dropdown.Toggle>

                <Dropdown.Menu>
                <Dropdown.Item eventKey="All" onSelect={this.onSelelectFilterSize}>all</Dropdown.Item>
                <Dropdown.Item eventKey="tech" onSelect={this.onSelelectFilterSize}>Tech</Dropdown.Item>
                <Dropdown.Item eventKey="airlines" onSelect={this.onSelelectFilterSize}>Airlines</Dropdown.Item>
                </Dropdown.Menu>
            </Dropdown>

            <DisplayList list={this.props.list.filter(this.matchesFilterSize)}/>
            </div>
        )
    }
}

export default FilteredList;
