import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import Fruit from './fruit.js';

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      items: [
      {name: "Apples", color: "Red", price: "$1"},
      {name: "Pears", color: "Green", price: "$2"},
      {name: "Oranges", color: "Orange", price: "$1"}
      ]
    };
  }

  changeFruit() {
    this.setState(
      {name: "Bananna"})
  }

  render() {
    return (
      <div className="main">
        {this.state.items.map(
          (fruit) => <Fruit name={fruit.name}
                            color={fruit.color}
                            price={fruit.price}></Fruit>
        )
        }
        <button onClick='changeFruit'>Hello</button>
      </div>
    )
  }
}

export default App;
