import React from 'react';
import ReactDOM from 'react-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Dropdown } from 'react-bootstrap';
import { Nav, Navbar, NavDropdown, Form, FormControl, Button } from 'react-bootstrap';
import DisplayList from './DisplayList.js'

class FilteredList extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            sector: "All",
            perform: "All",
            sort: "None"
        };
    }

    onSelectFilterSector = event => {
        this.setState ({
            sector: event
        })
    };

    onSelectSort = event => {
        this.setState ({
            sort: event
        })
    };

    matchesFilterSector = item =>  {
        if(this.state.sector === "All") { 
            return true
        } else if (this.state.sector === item.sector) {
            return true
        } else {
            return false
        }
    }

    matchesSortPrice1(a,b) {
        var nameA = a.name.toUpperCase();
        var nameB = b.name.toUpperCase();
        if (nameA < nameB) {
          return -1;
        }
        if (nameA > nameB) {
          return 1;
        }
        return 0;
    };

    displayMe(myList) {
        var ret = myList
        if(this.state.sector !== "All") {
            ret = ret.filter(this.matchesFilterSector)
        }
        if(this.state.perform !== "All") {
            // ret = ret.filter(this.)
        }
        if(this.state.sort === "None") {
            return ret
        }
        else if(this.state.sort === "Price1") {
            ret.sort(this.matchesSortPrice1)
        }
    }

    render() {
        console.log(this.props.list.map(e => e.name))
        return (
            <div>
            {/* <Dropdown>
                <Dropdown.Toggle variant="success" id="dropdown-basic">
                Filter By Sector
                </Dropdown.Toggle>

                <Dropdown.Menu>
                    <Dropdown.Item eventKey="All" onSelect={this.onSelectFilterSector}>All Companies</Dropdown.Item>
                    <Dropdown.Item eventKey="Technology" onSelect={this.onSelectFilterSector}>Technology</Dropdown.Item>
                    <Dropdown.Item eventKey="Airline" onSelect={this.onSelectFilterSector}>Airlines</Dropdown.Item>
                    <Dropdown.Item eventKey="Food" onSelect={this.onSelectFilterSector}>Food</Dropdown.Item>
                </Dropdown.Menu>
            </Dropdown>


            <DisplayList list={this.props.list.filter(this.matchesFilterSector)}/>
            <DisplayList list={this.props.list.sort(this.matchesSortPrice)}/> */}

            <Navbar bg="light" expand="lg">
                <Navbar.Brand>Filter Options</Navbar.Brand>
                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="mr-auto">
                        <NavDropdown title="Stock Performance" id="sector">
                            <NavDropdown.Item>Positive</NavDropdown.Item>
                            <NavDropdown.Item>Negative</NavDropdown.Item>
                            <NavDropdown.Divider/>
                            <NavDropdown.Item>All</NavDropdown.Item>
                        </NavDropdown>
                        <NavDropdown title="Sector" id="sector">
                            <NavDropdown.Item eventKey="Food" onSelect={this.onSelectFilterSector}>Food</NavDropdown.Item>
                            <NavDropdown.Item eventKey="Technology" onSelect={this.onSelectFilterSector}>Technology</NavDropdown.Item>
                            <NavDropdown.Item eventKey="Airline" onSelect={this.onSelectFilterSector}>Airlines</NavDropdown.Item>
                            <NavDropdown.Divider/>
                            <NavDropdown.Item eventKey="All" onSelect={this.onSelectFilterSector}>All</NavDropdown.Item>
                        </NavDropdown>
                        <NavDropdown title="Sort By" id="sort_by">
                            <NavDropdown.Item eventKey="Price1" onSelect={this.onSelectFilterSector}>Price (Highest to Lowest)</NavDropdown.Item>
                            <NavDropdown.Item>Price (Lowest to Highest)</NavDropdown.Item>
                            <NavDropdown.Divider />
                            <NavDropdown.Item>Name (A to Z)</NavDropdown.Item>
                            <NavDropdown.Item>Name (Z to A)</NavDropdown.Item>
                            <NavDropdown.Divider />
                            <NavDropdown.Item>Reset</NavDropdown.Item>
                        </NavDropdown>
                        <Nav.Link>Reset All</Nav.Link>
                    </Nav>
                </Navbar.Collapse>
            </Navbar>

            <DisplayList list={this.displayMe(this.props.list)}/>  
            </div>
        )
    }
}

export default FilteredList;
